mapper.insert:
        	/* 게시물 업로드 */
			INSERT INTO sns_post (mbr_cd, sp_cn)
			VALUES (#{mbrCd}, #{spCn})

mapper.select:
/* myfeed - 게시물 숫자 */
SELECT COUNT(*)
FROM sns_post
WHERE mbr_cd = #{mbrCd}

/* myfeed - 팔로워 숫자 */
SELECT COUNT(*)
FROM follow
WHERE following_mbr_cd = #{mbrCd}

/* myfeed - 팔로우 숫자 */
SELECT COUNT(*)
FROM follow
WHERE follow_mbr_cd = #{mbrCd}

/* myfeed - 게시물 대표이미지 */
SELECT sp_cd, mbr_cd, sp_cn, sp_reg_ymdt
FROM sns_post
WHERE mbr_cd = #{mbrCd}
ORDER BY sp_reg_ymdt DESC

/* myfeed - 프로필 수정 내역 조회 */
SELECT mbr_cd, mbr_email, mbr_nknm, mbr_profile
FROM member
WHERE mbr_cd = #{mbrCd}

/* myfeed - 프로필 수정 - 닉네임 유효성 검증 */
SELECT COUNT(*)
FROM member
WHERE mbr_nknm = #{mbrNknm}
  AND mbr_cd <> #{excludeMbrCd}

mapper.update:
/* myfeed - 프로필 수정 - 업데이트 */
UPDATE member

  
    mbr_nknm = #{mbrNknm},
  
  
    mbr_profile = #{mbrProfile},
  

WHERE mbr_cd = #{mbrCd}

mapper.select:
/* myfeed - 프로필 수정 - 비밀번호 유효성 검증(해시조회) */
SELECT mbr_pw
FROM member
WHERE mbr_cd = #{mbrCd}

mapper.update:
/* myfeed - 프로필 수정 - 비밀번호 변경 */
UPDATE member
SET mbr_pw = #{mbrPw}
WHERE mbr_cd = #{mbrCd}

mapper.select:
/* myfeed - 팔로우 여부  */
SELECT COUNT(*) FROM follow
WHERE follow_mbr_cd = #{me} AND following_mbr_cd = #{target}

mapper.insert:
/* myfeed - 팔로우  */
INSERT INTO follow (follow_mbr_cd, following_mbr_cd, follow_ymdt)
VALUES (#{me}, #{target}, NOW())

mapper.delete:
/* myfeed - 팔로우 삭제  */
DELETE FROM follow
WHERE follow_mbr_cd = #{me} AND following_mbr_cd = #{target}
